protected boolean focusAccepts(Object o) {
        UURI u = UURI.from(o);
        if (u == null)  return false;
        String host = u.getReferencedHost();        
        if (host==null) {
            System.out.println(&quot;HostScope.getHost(): null Host from URI: 
&quot;+u.getURI());
            host = &quot;&quot;;
