              disposition chain:&lt;br&gt;
              &lt;br&gt;
              &lt;bean
              class=&quot;org.archive.crawler.postprocessor.ReschedulingProcessor&quot;&gt;&lt;br&gt;
              &lt;property name=&quot;rescheduleDelaySeconds&quot; value=&quot;21600&quot;
              /&gt;&lt;br&gt;
              &lt;/bean&gt;&lt;br&gt;
