&gt;&gt;&gt;&gt;&gt;     &lt;/bean&gt;
&gt;&gt;&gt;&gt;&gt;     &lt;!-- &lt;bean id=&quot;rescheduler&quot; class=&quot;org.archive.crawler.postprocessor.ReschedulingProcessor&quot;&gt;
&gt;&gt;&gt;&gt;&gt;           &lt;property name=&quot;rescheduleDelaySeconds&quot; value=&quot;-1&quot; /&gt;
&gt;&gt;&gt;&gt;&gt;          &lt;/bean&gt; --&gt;
&gt;&gt;&gt;&gt;&gt;     &lt;!-- now, processors are assembled into ordered DispositionChain bean --&gt;
