&gt; &lt;bean id=&quot;acceptSurts&quot; class=&quot;org.archive.modules.deciderules.surt.SurtPrefixedDecideRule&quot;&gt;
&gt;    &lt;!-- &lt;property name=&quot;decision&quot; value=&quot;ACCEPT&quot;/&gt; --&gt;
&gt;    &lt;property name=&quot;seedsAsSurtPrefixes&quot; value=&quot;true&quot; /&gt;
