&gt;&gt;     value=&quot;archiveit.prod.9876-20150704232858960&quot;/&gt;
&gt;&gt;         &lt;property name=&quot;shouldProcessRule&quot;&gt;
&gt;&gt;                 &lt;bean
&gt;&gt;     class=&quot;org.archive.modules.deciderules.ExpressionDecideRule&quot;&gt;
&gt;&gt;                         &lt;property name=&quot;decision&quot; value=&quot;REJECT&quot;/&gt;
